import React, { Component } from 'react';
import go from 'gojs';
//import './App.css';
import './figure';
import dataModels from './modelsDfd'
import functionDfd from './functionsDfd'
import nodeTemplate from './nodeTemplate'

const $ = go.GraphObject.make;
var myDiagram;
export default class GoJs extends Component {
  constructor(props) {
    super(props);
    this.renderCanvas = this.renderCanvas.bind(this);
    this.state = { myModel: null, myDiagram: null }
  }

  renderCanvas() {
    myDiagram = $(go.Diagram, this.refs.goJsDiv, {
      grid: $(go.Panel, "Grid",
        $(go.Shape, "LineH", { stroke: "gray", strokeWidth: .9 }),
        $(go.Shape, "LineH", { stroke: "darkslategray", strokeWidth: 1.5, interval: 10 }),
        $(go.Shape, "LineV", { stroke: "gray", strokeWidth: .9 }),
        $(go.Shape, "LineV", { stroke: "darkslategray", strokeWidth: 1.5, interval: 10 })
      ),
      "LinkDrawn": functionDfd.showLinkLabel,  // this DiagramEvent listener is defined below
      "LinkRelinked": functionDfd.showLinkLabel,
      "toolManager.mouseWheelBehavior": go.ToolManager.WheelZoom,
      "draggingTool.isGridSnapEnabled": true,
      "undoManager.isEnabled": true  // enable undo & redo
    }
    );

    // define the Node templates for regular nodes
    myDiagram.nodeTemplateMap.add("", nodeTemplate.defaultGraph());  // the default category
    myDiagram.nodeTemplateMap.add("Conditional", nodeTemplate.conditionalGraph());
    myDiagram.nodeTemplateMap.add("Start", nodeTemplate.startGraph());
    myDiagram.nodeTemplateMap.add("Var", nodeTemplate.varGraph());
    myDiagram.nodeTemplateMap.add("If", nodeTemplate.ifGraph());
    myDiagram.nodeTemplateMap.add("case", nodeTemplate.caseGraph());
    myDiagram.nodeTemplateMap.add("switch", nodeTemplate.switchGraph());

    myDiagram.nodeTemplateMap.add("for",
      $(go.Node, "Spot",
        $(go.Shape, "Rectangle",
          {
            fill: "",
            minSize: new go.Size(140, 40),
            strokeWidth: 0
          }),
        $(go.Shape, "TriangleLeft", { width: 20, height: 40, fill: "black", alignment: go.Spot.Left }),
        $(go.Shape, "Rectangle", { width: 120, height: 40, fill: "black" }),
        $(go.Shape, "TriangleRight", { width: 20, height: 40, fill: "black", alignment: go.Spot.Right }),
        $(go.TextBlock, functionDfd.textStyle(), { text: "Inicio", font: "bold 8pt Helvetica, Arial, sans-serif", position: new go.Point(0, 0) }),
        $(go.TextBlock, functionDfd.textStyle(), { editable: true, text: "3", font: "bold 8pt Helvetica, Arial, sans-serif", position: new go.Point(200, 0) }),
        functionDfd.makePort("B", go.Spot.Bottom, go.Spot.Bottom, true, false)
      ));
    myDiagram.nodeTemplateMap.add("End",
      $(go.Node, "Table", functionDfd.nodeStyle(),
        $(go.Panel, "Auto",
          $(go.Shape, "Circle", {
            minSize: new go.Size(40, 40),
            fill: "#DC3C00",
            strokeWidth: 0,
            portId: "",
            fromLinkable: true, toLinkable: true
          }),
          $(go.TextBlock, "End", functionDfd.textStyle(),
            new go.Binding("text"))
        ),
        // three named ports, one on each side except the bottom, all input only:
        functionDfd.makePort("T", go.Spot.Top, go.Spot.Top, false, true),
        functionDfd.makePort("L", go.Spot.Left, go.Spot.Left, false, true),
        functionDfd.makePort("R", go.Spot.Right, go.Spot.Right, false, true)
      ));
    // taken from ../extensions/Figures.js:
    go.Shape.defineFigureGenerator("File", function (shape, w, h) {
      var geo = new go.Geometry();
      var fig = new go.PathFigure(0, 0, true); // starting point
      geo.add(fig);
      fig.add(new go.PathSegment(go.PathSegment.Line, .75 * w, 0));
      fig.add(new go.PathSegment(go.PathSegment.Line, w, .25 * h));
      fig.add(new go.PathSegment(go.PathSegment.Line, w, h));
      fig.add(new go.PathSegment(go.PathSegment.Line, 0, h).close());
      var fig2 = new go.PathFigure(.75 * w, 0, false);
      geo.add(fig2);
      // The Fold
      fig2.add(new go.PathSegment(go.PathSegment.Line, .75 * w, .25 * h));
      fig2.add(new go.PathSegment(go.PathSegment.Line, w, .25 * h));
      geo.spot1 = new go.Spot(0, .25);
      geo.spot2 = go.Spot.BottomRight;
      return geo;
    });
    myDiagram.nodeTemplateMap.add("Comment",
      $(go.Node, "Auto", functionDfd.nodeStyle(),
        $(go.Shape, "File", {
          fill: "#DEE0A3",
          strokeWidth: 0,
          portId: "",
          fromLinkable: true, toLinkable: true
        }),
        $(go.TextBlock, functionDfd.textStyle(), {
          margin: 5,
          maxSize: new go.Size(200, NaN),
          wrap: go.TextBlock.WrapFit,
          textAlign: "center",
          editable: true,
          font: "bold 12pt Helvetica, Arial, sans-serif",
          stroke: '#454545'
        },
          new go.Binding("text").makeTwoWay())
        // no ports, because no links are allowed to connect with a comment
      ));


    myDiagram.groupTemplate =
      $(go.Group, "Auto", {
        resizable: true,
        resizeObjectName: "PH"
      },
        $(go.Panel, "Auto",
          $(go.Shape, "RoundedRectangle",
            {
              fill: "rgba(128,128,128,0.33)",
              name: "PH",
            },
            new go.Binding("desiredSize", "size", go.Size.parse).makeTwoWay(go.Size.stringify)),
          $(go.Placeholder,    // represents the area of all member parts,
            { padding: 3 }),
          $(go.TextBlock, { alignment: go.Spot.Right, font: "Bold 12pt Sans-Serif" },
            new go.Binding("text"))
        ),

        //three named ports, one on each side except the top, all output only:
        functionDfd.makePort("T", go.Spot.Top, go.Spot.Top, false, true),
        functionDfd.makePort("B", go.Spot.Bottom, go.Spot.Bottom, true, false)
      );


    // replace the default Link template in the linkTemplateMap
    myDiagram.linkTemplate = functionDfd.linkTemplate();

    // temporary links used by LinkingTool and RelinkingTool are also orthogonal:
    myDiagram.toolManager.linkingTool.temporaryLink.routing = go.Link.Orthogonal;
    myDiagram.toolManager.relinkingTool.temporaryLink.routing = go.Link.Orthogonal;

    // initialize the Palette that is on the left side of the page

    let myPalette = $(go.Palette, "Diamond", // must name or refer to the DIV HTML element
      {
        layout: $(go.GridLayout, { alignment: go.GridLayout.Location }),
        nodeTemplateMap: myDiagram.nodeTemplateMap, // share the templates used by myDiagram
        model: new go.GraphLinksModel(dataModels.modelsData(), dataModels.modelsLinks())
      });

    myPalette.layout.sorting = go.GridLayout.Forward;

  }


  componentDidMount() {
    //console.log('componentDidMount')
    this.renderCanvas();
  }


  componentWillUpdate(prevProps) {
    /*console.log(JSON.stringify(prevProps, null, 2))
  if (this.props.data !== prevProps.data) {
     console.log('Updating 2');
     const model = this.state.myModel;
     const diagram = this.state.myDiagram;
     model.nodeDataArray = this.props.data;
     diagram.model = model;
     this.setState({ myModel: model, myDiagram: diagram });
   }*/
  }
  onClickChange() {
    console.log(myDiagram.model.toJson());
  }
  render() {
    return (
      <div className='container-fluid'>
        <div className='row'>
          <div className='col-md-3' id='Diamond' style={{ 'backgroundColor': '#373737' }}></div>
          <div className='col-md-9 row'>
            <div id='blockWhite' ></div>
            <div ref="goJsDiv" id='goJsDiv' className='col-md-12' style={{ 'width': '500px', 'height': '700px', 'backgroundColor': '#373737' }}>
            </div>
          </div>
        </div>
        <div className='row'>
          <button className='btn btn-primary' onClick={this.onClickChange}>Save</button>
        </div>
      </div>
    )
  }
}
